mapper.update:
 	UPDATE "MEMBER" SET
	MEMBER_NICKNAME = #{memberNickname},
	MEMBER_TEL= #{memberTel},
	MEMBER_ADDRESS = #{memberAddress},
	MEMBER_PW = #{memberPw}
	WHERE MEMBER_NO = #{memberNo}

 	UPDATE "MEMBER" SET
	MEMBER_NICKNAME = #{memberNickname},
	MEMBER_TEL= #{memberTel},
	MEMBER_ADDRESS = #{memberAddress}
	WHERE MEMBER_NO = #{memberNo}

UPDATE "MEMBER" SET
MANNER = MANNER + (0.2 * #{goodResult})
WHERE MEMBER_NO = #{reviewBuyerNo}

UPDATE "MEMBER" SET
MANNER = MANNER - (0.2 * #{badResult})
WHERE MEMBER_NO = #{reviewBuyerNo}

mapper.select:
SELECT RATING_NO, M.MEMBER_NO, SELLER_NO, R.GOODS_NO, RECEIVER_NO, MESSAGE,
CASE WHEN SYSDATE - R.CREATED_AT < 1/24/60 THEN FLOOR( (SYSDATE - R.CREATED_AT) * 24 * 60 * 60 ) || '초 전' WHEN SYSDATE - R.CREATED_AT < 1/24 THEN FLOOR( (SYSDATE - R.CREATED_AT) * 24 * 60) || '분 전' WHEN SYSDATE - R.CREATED_AT < 1 THEN FLOOR( (SYSDATE - R.CREATED_AT) * 24) || '시간 전' WHEN SYSDATE - R.CREATED_AT < 30 THEN FLOOR(SYSDATE - R.CREATED_AT) || '일 전' WHEN SYSDATE - R.CREATED_AT < 365 THEN FLOOR((SYSDATE-R.CREATED_AT)/30) || '달 전' ELSE TO_CHAR(R.CREATED_AT, 'YYYY-MM-DD') END CREATED_AT , BUYER_NO, MEMBER_NICKNAME,PROFILE_IMG, SUBSTR(MEMBER_ADDRESS,11,3) MEMBER_ADDRESS FROM REVIEW R JOIN "MEMBER" M ON (M.MEMBER_NO = R.MEMBER_NO) JOIN GOODS_SELL G ON(G.GOODS_NO = R.GOODS_NO)
WHERE RECEIVER_NO = #{memberNo}
WHERE RECEIVER_NO = #{memberNo} AND SELLER_NO = #{memberNo}
WHERE RECEIVER_NO = #{memberNo} AND BUYER_NO = #{memberNo}
ORDER BY RATING_NO DESC

SELECT COUNT(*) FROM( SELECT RATING_NO, M.MEMBER_NO, SELLER_NO, R.GOODS_NO, RECEIVER_NO, MESSAGE,
CASE WHEN SYSDATE - R.CREATED_AT < 1/24/60 THEN FLOOR( (SYSDATE - R.CREATED_AT) * 24 * 60 * 60 ) || '초 전' WHEN SYSDATE - R.CREATED_AT < 1/24 THEN FLOOR( (SYSDATE - R.CREATED_AT) * 24 * 60) || '분 전' WHEN SYSDATE - R.CREATED_AT < 1 THEN FLOOR( (SYSDATE - R.CREATED_AT) * 24) || '시간 전' WHEN SYSDATE - R.CREATED_AT < 30 THEN FLOOR(SYSDATE - R.CREATED_AT) || '일 전' WHEN SYSDATE - R.CREATED_AT < 365 THEN FLOOR((SYSDATE-R.CREATED_AT)/30) || '달 전' ELSE TO_CHAR(R.CREATED_AT, 'YYYY-MM-DD') END CREATED_AT , BUYER_NO, MEMBER_NICKNAME,PROFILE_IMG, SUBSTR(MEMBER_ADDRESS,11,3) MEMBER_ADDRESS FROM REVIEW R JOIN "MEMBER" M ON (M.MEMBER_NO = R.MEMBER_NO) JOIN GOODS_SELL G ON(G.GOODS_NO = R.GOODS_NO)
WHERE RECEIVER_NO = #{memberNo}
WHERE RECEIVER_NO = #{memberNo} AND SELLER_NO = #{memberNo}
WHERE RECEIVER_NO = #{memberNo} AND BUYER_NO = #{memberNo}
ORDER BY RATING_NO DESC )
